mapper.sql:
FROM t_group_role_user_rel grur, t_user tu, t_group_role gr WHERE grur.user_id = tu.ID and grur.group_role_id = gr.id and grur.group_id=#{groupId}
and grur.active_status=1
and grur.active_status=0 and grur.create_time::timestamp + '15 day' <= now()
and grur.active_status=#{ativeStatus}
and gr.id=#{roleId}
and tu.user_name like CONCAT('%','${keywords}','%' )
